package day10;

public class StringDemo4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="JavaSE";
		char [] ch=s.toCharArray();
		for(int i=0;i<ch.length;i++){
			System.out.println(ch[i]);
		}
		System.out.println(s.toUpperCase());
	}

}
